import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';


// describe number five fingers
const FingerFiveDescription = new GestureDescription('FingerFive');


// thumb:
FingerFiveDescription.addCurl(Finger.Thumb, FingerCurl.NoCurl, 0.75);
FingerFiveDescription.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 0.75);
FingerFiveDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 0.75);
FingerFiveDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpRight, 0.75);

// index:
FingerFiveDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.75);
FingerFiveDescription.addDirection(Finger.Index, FingerDirection.VerticalUp, 0.75);
FingerFiveDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpLeft, 0.75);
FingerFiveDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpRight, 0.75);

// middle:
FingerFiveDescription.addCurl(Finger.Middle, FingerCurl.NoCurl, 0.75);
FingerFiveDescription.addDirection(Finger.Middle, FingerDirection.VerticalUp, 0.75);
FingerFiveDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpLeft, 0.75);
FingerFiveDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpRight, 0.75);

// ring:
FingerFiveDescription.addCurl(Finger.Ring, FingerCurl.NoCurl, 0.75);
FingerFiveDescription.addDirection(Finger.Ring, FingerDirection.VerticalUp, 0.75);
FingerFiveDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpLeft, 0.75);
FingerFiveDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpRight, 0.75);

// pinky:
FingerFiveDescription.addCurl(Finger.Pinky, FingerCurl.NoCurl, 0.75);
FingerFiveDescription.addDirection(Finger.Pinky, FingerDirection.VerticalUp, 0.75);
FingerFiveDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpLeft, 0.75);
FingerFiveDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpRight, 0.75);

// give additional weight to index finger
//FingerFiveDescription.setWeight(Finger.Index, 1.5);
//FingerFiveDescription.setWeight(Finger.Middle, 1.5);
//FingerFiveDescription.setWeight(Finger.Ring, 1.5);
//FingerFiveDescription.setWeight(Finger.Pinky, 1.5);

export default FingerFiveDescription;
