import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';


// describe number three fingers
const FingerOneDescription = new GestureDescription('FingerThree');


// thumb:
FingerOneDescription.addCurl(Finger.Thumb, FingerCurl.HalfCurl, 0.5);
FingerOneDescription.addCurl(Finger.Thumb, FingerCurl.NoCurl, 0.5);
FingerOneDescription.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 0.75);
//FingerOneDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 0.75);

// index:
FingerOneDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.75);
FingerOneDescription.addDirection(Finger.Index, FingerDirection.VerticalUp, 0.75); 
//FingerOneDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpLeft, 0.75);
//FingerOneDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpRight, 0.75);

// middle:
FingerOneDescription.addCurl(Finger.Middle, FingerCurl.NoCurl, 0.75);
FingerOneDescription.addDirection(Finger.Middle, FingerDirection.VerticalUp, 0.75);
//FingerOneDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpLeft, 0.75);
//FingerOneDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpRight, 0.75);

// ring:
FingerOneDescription.addCurl(Finger.Ring, FingerCurl.NoCurl, 1);
FingerOneDescription.addDirection(Finger.Ring, FingerDirection.VerticalUp, 1);
FingerOneDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpLeft, 1);
FingerOneDescription.addDirection(Finger.Ring, FingerDirection.DiagonalUpRight, 1);

// pinky:
FingerOneDescription.addCurl(Finger.Pinky, FingerCurl.FullCurl, 0.75);
//FingerOneDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpLeft, 0.75);
//FingerOneDescription.addDirection(Finger.Pinky, FingerDirection.HorizontalLeft, 0.2);

// give additional weight to index finger
//FingerOneDescription.setWeight(Finger.Index, 2);
//FingerOneDescription.setWeight(Finger.Middle, 2);
//FingerOneDescription.setWeight(Finger.Ring, 2);

export default FingerOneDescription;
