import { Finger, FingerCurl, FingerDirection } from 'fingerpose/src/FingerDescription';
import GestureDescription from 'fingerpose/src/GestureDescription';


// describe number two gesture
const FingerTwoDescription = new GestureDescription('FingerTwo');


// thumb:
FingerTwoDescription.addCurl(Finger.Thumb, FingerCurl.HalfCurl, 0.5);
FingerTwoDescription.addCurl(Finger.Thumb, FingerCurl.NoCurl, 0.5);
FingerTwoDescription.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 0.75);
FingerTwoDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 0.75);

// index:
FingerTwoDescription.addCurl(Finger.Index, FingerCurl.NoCurl, 0.75);
FingerTwoDescription.addDirection(Finger.Index, FingerDirection.VerticalUp, 0.75);
FingerTwoDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpLeft, 0.75);
FingerTwoDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpRight, 0.75);
//FingerTwoDescription.addDirection(Finger.Index, FingerDirection.DiagonalUpRight, 0.75);

// middle:
FingerTwoDescription.addCurl(Finger.Middle, FingerCurl.NoCurl, 0.75);
FingerTwoDescription.addDirection(Finger.Middle, FingerDirection.VerticalUp, 0.75);
FingerTwoDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpLeft, 0.75);
FingerTwoDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpRight, 0.75);
//FingerTwoDescription.addDirection(Finger.Middle, FingerDirection.DiagonalUpRight, 0.75);

// ring:
FingerTwoDescription.addCurl(Finger.Ring, FingerCurl.FullCurl, 0.75);
FingerTwoDescription.addDirection(Finger.Ring, FingerDirection.VerticalUp, 0.5);
//FingerTwoDescription.addDirection(Finger.Ring, FingerDirection.HorizontalLeft, 0.2);

// pinky:
FingerTwoDescription.addCurl(Finger.Pinky, FingerCurl.FullCurl, 0.75);
FingerTwoDescription.addDirection(Finger.Pinky, FingerDirection.VerticalUp, 0.5);
//FingerTwoDescription.addDirection(Finger.Pinky, FingerDirection.DiagonalUpLeft, 1.0);
//FingerTwoDescription.addDirection(Finger.Pinky, FingerDirection.HorizontalLeft, 0.2);

// give additional weight to index and middle fingers
FingerTwoDescription.setWeight(Finger.Index, 2);
FingerTwoDescription.setWeight(Finger.Middle, 2);

export default FingerTwoDescription;
