import { Finger, FingerCurl, FingerDirection } from '../FingerDescription';
import GestureDescription from '../GestureDescription';


// describe OK gesture 
const OkDescription = new GestureDescription('ok');

// thumb
OkDescription.addCurl(Finger.Thumb, FingerCurl.HalfCurl, 0.75);
OkDescription.addCurl(Finger.Thumb, FingerCurl.FullCurl, 0.75); // 0.5
OkDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalLeft, 0.75);
OkDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 0.75);
OkDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpRight, 0.75);
OkDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalRight, 0.75);
OkDescription.addDirection(Finger.Thumb, FingerDirection.VerticalUp, 0.75);

// index
OkDescription.addCurl(Finger.Index, FingerCurl.HalfCurl, 0.85);
OkDescription.addCurl(Finger.Index, FingerCurl.FullCurl, 0.85);
OkDescription.addDirection(Finger.Index, FingerDirection.DiagonalDownLeft, 0.75);
OkDescription.addDirection(Finger.Index, FingerDirection.HorizontalLeft, 0.75);
OkDescription.addDirection(Finger.Index, FingerDirection.HorizontalRight, 0.75);
OkDescription.addDirection(Finger.Index, FingerDirection.VerticalDown, 0.75);
//OkDescription.addDirection(Finger.Index, FingerDirection.HorizontalRight, 0.75);
// all other fingers:
// - curled
// - horizontal left or right
for(let finger of [Finger.Middle, Finger.Ring, Finger.Pinky]) {
  OkDescription.addCurl(finger, FingerCurl.NoCurl, 0.75);
  OkDescription.addCurl(finger, FingerCurl.HalfCurl, 0.75);
  OkDescription.addDirection(finger, FingerDirection.VerticalUp, 0.85); 
  OkDescription.addDirection(finger, FingerDirection.DiagonalUpLeft, 0.85); 
  OkDescription.addDirection(finger, FingerDirection.DiagonalUpRight, 0.85);
}

//OkDescription.setWeight(Finger.Thumb, 2);
//OkDescription.setWeight(Finger.Index, 2);

export default OkDescription;