import { Finger, FingerCurl, FingerDirection } from 'fingerpose/src/FingerDescription';
import GestureDescription from 'fingerpose/src/GestureDescription';


// describe thumbs left
const thumbsLeftDescription = new GestureDescription('thumbs_left');

// thumb
thumbsLeftDescription.addCurl(Finger.Thumb, FingerCurl.NoCurl, 0.5);
// 주의, 캠 좌우반전이므로 좌,우 반대로 줘야 함.
thumbsLeftDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalRight, 0.5);
thumbsLeftDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpRight, 0.5);

// all other fingers:
// - curled
// - horizontal left or right
for(let finger of [Finger.Index, Finger.Middle, Finger.Ring, Finger.Pinky]) {
  thumbsLeftDescription.addCurl(finger, FingerCurl.FullCurl, 0.5);
}

export default thumbsLeftDescription;
