import { Finger, FingerCurl, FingerDirection } from 'fingerpose/src/FingerDescription';
import GestureDescription from 'fingerpose/src/GestureDescription';


// describe thumbs right
const thumbsRightDescription = new GestureDescription('thumbs_right');

// thumb
thumbsRightDescription.addCurl(Finger.Thumb, FingerCurl.NoCurl, 0.5);
// 주의, 캠 좌우반전이므로 좌,우 반대로 줘야 함.
thumbsRightDescription.addDirection(Finger.Thumb, FingerDirection.HorizontalLeft, 0.5);
thumbsRightDescription.addDirection(Finger.Thumb, FingerDirection.DiagonalUpLeft, 0.5);

// all other fingers:
// - curled
// - horizontal left or right
for(let finger of [Finger.Index, Finger.Middle, Finger.Ring, Finger.Pinky]) {
    thumbsRightDescription.addCurl(finger, FingerCurl.FullCurl, 0.5);
}

export default thumbsRightDescription;
